function processLoginForm(){
	return function (returnData){
		if (returnData != null){
			if ($(returnData).find("input[id=ucTop_btnLogin]").length > 0){
				if ($(returnData).find("input[id=ucTop_btnLogin]").val() == "Logga ut"){
					customAlert('true in processLoginForm');
					return true;
					}
				}
			}
		else{
			customAlert('false in processLoginForm');
			return false;
			}
		}
	}

function setLanguage(lang){
	
	//Get browser language
		browser_language = $.localise.defaultLanguage.substring(0, 2);

	//No language has been previously selected, set a default language
		if (getLawnchair("language") == null){
			setLawnchair("language", $.localise.defaultLanguage);
			}

	//If variable lang is provided
		if (typeof(lang) !== "undefined"){
			setLawnchair("language", lang);
			}

	//Load language into items
		$.localise("lang", {language: getLawnchair("language"), path: "languages/"});
		$("body").addClass("ui-loading");
		$("[lang-id]").each(function(){
			
			//Exception for collapsible headings
				if ($(this).hasClass("ui-collapsible-heading")){
					$("a", this).html(language[$(this).attr("lang-id")]+"<span class='ui-collapsible-heading-status'> click to expand contents</span>");
					}
			
			//Else
				else{
					$(this).text(language[$(this).attr("lang-id")]);
					}
			});
		$("body").removeClass("ui-loading");
	}

function customAlert(message, vibrate){
	navigator.notification.alert(message, function(){}, "");
	if (typeof(vibrate) !== "undefined" && vibrate == true){
		navigator.notification.vibrate(1000);
		}

	}

function onDeviceReady(){
	
	//Add slight header margin for iOS 7
		if (device.platform == 'iOS' && device.version >= '7.0') {
			document.body.style.marginTop = "20px";
			}

	//Flush database if not latest version
		if (getLawnchair("database_ver") != database_ver){
			clearLawnchair();
			setLawnchair("database_ver", database_ver);
			}

	//Set language
		setLanguage();
		$("#language_selector").change(function(){
			setLanguage($(this).val());
			});

		//Populate language selector
			$(available_languages).each(function(){
				var option = $("<option>")
					.appendTo($("#language_selector"))
					.val(this.code)
					.text(this.lable)
					.prop("selected", (getLawnchair("language") == this.code ? true : false))
				});

	//Navbar/footer
		$(function() {
			$( "[data-role='navbar']" ).navbar({disabled: true});
			$( "[data-role='header'], [data-role='footer']" ).toolbar();
		});

		$(document).on("pageshow", "[data-role='page']", function(){
			
			// Remove active class from nav buttons
				$("[data-role='navbar'] a.ui-btn-active").removeClass("ui-btn-active");
			
			// Add active class to current nav button
				current_page = $(this).attr("id");
				$("[data-role='navbar'] a").each(function(){
					if ($(this).attr("href") === "#"+current_page){
						$(this).addClass("ui-btn-active");
						}
					});
			});

	//Check UUID
		checkUUID();

	//Set initial splash page
		location.hash = "#splash";
		$("#progressbar").progressbar({
			value: 0,
			change: function(){
				$("#progressbar > .progress-label").text($("#progressbar").progressbar("value") + "%");
				}
			});
		$("#progressbar").progressbar("value", 1);

		spinnerShow(false, function(){fetchPages()});

	//Hide navbar on input/textarea focus
		$("input, textarea, select").on("focus", function(){
			$("div[data-role='footer']").hide();
			});

		$("input, textarea, select").on("blur", function(){
			$("div[data-role='footer']").show();
			});

	//Popup events
		$(document).on("popupbeforeposition", "#imagePopup, #pdfPopup, #mapPopup", function(){
			
			//Disable touch movement
				$("body").on("touchmove", false);
			 
			// Set a max-height & max-width to make large images shrink to fit the screen.
				var maxHeight = $(window).height() - 75 + "px";  // 68px: 2 * 15px for top/bottom tolerance, 38px for footer.
				var maxWidth = $(window).width() - 30 + "px";  // 30px: 2 * 15px for left/right tolerance.
				$("img.photo, iframe", this).css({
					"max-height": maxHeight,
					"max-width": maxWidth,
					});

			//If iframe (map popup), set height & width of iframe and map div (for Android)
				$("iframe", this).css({
					"height": maxHeight,
					"width": maxWidth,
					});
				$("iframe", this).contents().find("#map_canvas").css({
					"height": maxHeight,
					"width": maxWidth,
					});

			//Set popup tolerance
				$(this).popup("option", "tolerance", "15,15,53,15");

			});

		$(document).on("popupafteropen", "#imagePopup, #pdfPopup, #mapPopup", function(){
			 spinnerplugin.hide();
			})
		
		$(document).on("popupafterclose", "#imagePopup, #pdfPopup, #mapPopup", function(){
			
			//Enable touch movement
				$("body").off("touchmove");

			});

	//Sort list
		$(document).on("pagecreate", "#searchPage", function(){
			
			//Initiate sortable list
				$("#sort_list").sortable({
					axis: "y",
					start: function(event, ui){
						//Style active item
							ui.item.addClass("ui-btn-active");
						},
					stop: function(event, ui){
						//Unstyle active item
							ui.item.removeClass("ui-btn-active");
						
						// Refresh list to the end of sort to have a correct display
							$(this).listview("refresh");
						},
					});
				$("#sort_list").disableSelection();
			
			});

	//Show page loader during page switch
		$(document).on("pagebeforeshow", function(){
			//spinnerShow(true);
			});

		$(document).on("pageshow", function(){
			//spinnerplugin.hide();
			});
	}

function spinnerShow(overlay, callback){
	//spinnerplugin.show({overlay: overlay});
	if(typeof callback == "function"){
		setTimeout(function(){callback()}, 50);
		}
	}